import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import StudentForm from './StudentForm';
import AddMarksForm from './AddMarksForm';
import HomePage from './HomePage';
import UpdateMarksForm from './UpdateMarksForm';
import GetStudentMarksForm from './GetStudentMarksForm';
import GetStudentGradeForm from './GetStudentGradeForm';

const App = () => {
  return (
    <BrowserRouter>
      <div className="app-container">

        <style>
          {`
          .nav-links {
            display: flex;
            flex-direction: column;
            list-style: none;
            margin: 0;
            padding: 0;
            background-color: #333;
            height: 100vh;
            width: 200px;
            position: fixed;
            top: 0;
            left: 0;
          }

          .nav-links li {
            padding: 10px;
          }

          .nav-links li a {
            color: #fff;
            text-decoration: none;
          }

          .nav-links li a:hover {
            text-decoration: underline;
          }

          .main-content {
            margin-left: 200px;
            padding: 20px;
          }

          .header {
            display: flex;
            justify-content: center;
            align-items: center;
            background-color: #333;
            color: #fff;
            padding: 20px;
          }
          `}
        </style>
        <nav className="mt-1">
          <ul className="nav-links">
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/StudentForm">Student Management</Link>
            </li>
            <li>
              <Link to="/AddMarksForm">Assign Marks to Student</Link>
            </li>
            <li>
              <Link to="/UpdateMarksForm">Correct Student Marks</Link>
            </li>
            <li>
              <Link to="/GetStudentMarksForm">Student Result</Link>
            </li>
            <li>
              <Link to="/GetStudentGradeForm">Student Grade</Link>
            </li>
          </ul>
        </nav>
        <main className="main-content">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/StudentForm" element={<StudentForm />} />
            <Route path="/AddMarksForm" element={<AddMarksForm />} />
            <Route path="/UpdateMarksForm" element={<UpdateMarksForm />} />
            <Route path="/GetStudentMarksForm" element={<GetStudentMarksForm />} />
            <Route path="/GetStudentGradeForm" element={<GetStudentGradeForm />} />
            {/* Add more routes for other components if needed */}
          </Routes>
        </main>
        <footer>
          {/* Add your footer content here */}
        </footer>
      </div>
    </BrowserRouter>
  );
};

export default App;
