import React, { useState } from 'react';
import axios from 'axios';

const AddMarksForm = () => {
  const initialState = {
    telugu: '',
    hindi: '',
    english: '',
    maths: '',
    science: '',
    social: '',
    studentId: '',
  };

  const [marks, setMarks] = useState(initialState);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setMarks((prevMarks) => ({
      ...prevMarks,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const jsonData = JSON.stringify(marks);
    console.log(jsonData); // JSON output for testing purposes

    axios
      .post('http://localhost:8080/addMarks', jsonData, {
        headers: {
          'Content-Type': 'application/json',
        },
      })
      .then((response) => {
        console.log(response.data); // Optional: handle the response
        setMarks(initialState); // Clear the form inputs after successful submission
      })
      .catch((error) => {
        console.error(error); // Optional: handle the error
      });
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-sm mx-auto">
      <label className="flex flex-col mb-4">
        <span className="mb-1">Telugu:</span>
        <input
          type="number"
          name="telugu"
          value={marks.telugu}
          onChange={handleChange}
          className="p-2 border border-gray-300 rounded"
        />
      </label>
      {/* Repeat the same structure for other subjects */}
      {/* ... */}
      <button
        type="submit"
        className="py-2 px-4 mt-4 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        Submit
      </button>
    </form>
  );
};

export default AddMarksForm;
