import React, { useState, useEffect } from 'react';
import '/Users/premsundar/Downloads/projects/test/src/css/StudentForm.css';

const StudentForm = () => {
  const [studentID, setStudentID] = useState('');
  const [studentName, setStudentName] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [students, setStudents] = useState([]);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await fetch('http://localhost:8080/showAllStudents');
      if (response.ok) {
        const data = await response.json();
        setStudents(data);
      } else {
        console.error('Error fetching students');
      }
    } catch (error) {
      console.error('Network error:', error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const student = {
      studentId: studentID,
      studentName,
      studentMobileNumber: mobileNumber,
    };

    try {
      const response = await fetch('http://localhost:8080/addStudent', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(student),
      });

      if (response.ok) {
        console.log('Student data submitted successfully');
        setStudentID('');
        setStudentName('');
        setMobileNumber('');
        fetchStudents();
      } else {
        console.error('Error submitting student data');
      }
    } catch (error) {
      console.error('Network error:', error);
    }
  };

  const handleDelete = async (studentId) => {
    try {
      const response = await fetch(`http://localhost:8080/deleteStudent/${studentId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        console.log('Student deleted successfully');
        fetchStudents();
      } else {
        console.error('Error deleting student');
      }
    } catch (error) {
      console.error('Network error:', error);
    }
  };

  const studentListStyle = {
    listStyle: 'none',
    padding: '0',
  };

  const listItemStyle = {
    marginBottom: '10px',
  };

  const deleteButtonStyle = {
    marginLeft: '10px',
  };

  return (
    <div className="student-form-container">
      <form onSubmit={handleSubmit}>
        <div className="form-field">
          <label htmlFor="studentID">Student ID:</label>
          <input
            type="text"
            id="studentID"
            value={studentID}
            onChange={(event) => setStudentID(event.target.value)}
            required
          />
        </div>

        <div className="form-field">
          <label htmlFor="studentName">Student Name:</label>
          <input
            type="text"
            id="studentName"
            value={studentName}
            onChange={(event) => setStudentName(event.target.value)}
            required
          />
        </div>

        <div className="form-field">
          <label htmlFor="mobileNumber">Mobile Number:</label>
          <input
            type="text"
            id="mobileNumber"
            value={mobileNumber}
            onChange={(event) => setMobileNumber(event.target.value)}
            required
          />
        </div>

        <button type="submit">Submit</button>
      </form>

      <h2>All Students</h2>
      <ul className="student-list" style={studentListStyle}>
        {students.map((student) => (
          <li key={student.studentId} style={listItemStyle}>
            <span>{student.studentName} - {student.studentMobileNumber}</span>
            <button onClick={() => handleDelete(student.studentId)} style={deleteButtonStyle}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StudentForm;
