import React, { useState } from 'react';
import axios from 'axios';

const GetStudentMarksForm = () => {
  const [studentId, setStudentId] = useState('');
  const [studentMarks, setStudentMarks] = useState([]);

  const handleChange = (event) => {
    setStudentId(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    axios
      .get(`http://localhost:8080/getStudentMarks/${studentId}`)
      .then((response) => {
        setStudentMarks(response.data);
        setStudentId('');
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const markListStyle = {
    listStyle: 'none',
    padding: '0',
  };

  const markItemStyle = {
    marginBottom: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    padding: '10px',
  };

  const markLabelStyle = {
    fontWeight: 'bold',
    marginRight: '5px',
  };

  const formContainerStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center', // Added to horizontally center the form
    marginBottom: '10px',
  };

  const inputStyle = {
    marginRight: '10px',
  };

  return (
    <div>
      <form onSubmit={handleSubmit} style={formContainerStyle}>
        <label>
          Student ID:
          <input type="number" value={studentId} onChange={handleChange} style={inputStyle} />
        </label>
        <button type="submit">Fetch Marks</button>
      </form>

      <ul style={markListStyle}>
        {/* Display student marks */}
        {studentMarks.map((mark) => (
          <li key={mark.id} style={markItemStyle}>
            {/* Display mark details */}
            <p>
              <span style={markLabelStyle}>Telugu:</span>
              {mark.telugu}
            </p>
            <p>
              <span style={markLabelStyle}>Hindi:</span>
              {mark.hindi}
            </p>
            <p>
              <span style={markLabelStyle}>English:</span>
              {mark.english}
            </p>
            <p>
              <span style={markLabelStyle}>Maths:</span>
              {mark.maths}
            </p>
            <p>
              <span style={markLabelStyle}>Science:</span>
              {mark.science}
            </p>
            <p>
              <span style={markLabelStyle}>Social:</span>
              {mark.social}
            </p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default GetStudentMarksForm;
