import React, { useState } from 'react';
import axios from 'axios';

const MarksForm = () => {
  const [marks, setMarks] = useState({
    telugu: '',
    hindi: '',
    english: '',
    maths: '',
    science: '',
    social: '',
    studentId: ''
  });

  const handleChange = (e) => {
    setMarks({ ...marks, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post(`/updateMarks/${marks.studentId}`, marks)
      .then((response) => {
        alert('Marks updated successfully!');
        window.location.href = '/'; // Redirect to homepage
      })
      .catch((error) => {
        alert('Error updating marks: ' + error.response.data);
      });
  };

  return (
    <div>
      <h2>Update Marks</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="telugu">Telugu:</label>
        <input type="number" id="telugu" name="telugu" value={marks.telugu} onChange={handleChange} required /><br />

        <label htmlFor="hindi">Hindi:</label>
        <input type="number" id="hindi" name="hindi" value={marks.hindi} onChange={handleChange} required /><br />

        <label htmlFor="english">English:</label>
        <input type="number" id="english" name="english" value={marks.english} onChange={handleChange} required /><br />

        <label htmlFor="maths">Maths:</label>
        <input type="number" id="maths" name="maths" value={marks.maths} onChange={handleChange} required /><br />

        <label htmlFor="science">Science:</label>
        <input type="number" id="science" name="science" value={marks.science} onChange={handleChange} required /><br />

        <label htmlFor="social">Social:</label>
        <input type="number" id="social" name="social" value={marks.social} onChange={handleChange} required /><br />

        <label htmlFor="studentId">Student ID:</label>
        <input type="number" id="studentId" name="studentId" value={marks.studentId} onChange={handleChange} required /><br />

        <button type="submit">Update Marks</button>
      </form>
    </div>
  );
};

export default MarksForm;
