import React, { useState } from 'react';
import axios from 'axios';

const AddMarksForm = () => {
  const initialState = {
    telugu: '',
    hindi: '',
    english: '',
    maths: '',
    science: '',
    social: '',
    studentId: '',
  };

  const [marks, setMarks] = useState(initialState);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setMarks((prevMarks) => ({
      ...prevMarks,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const jsonData = JSON.stringify(marks);
    console.log(jsonData); // JSON output for testing purposes

    axios
      .post('http://localhost:8080/addMarks', jsonData, {
        headers: {
          'Content-Type': 'application/json',
        },
      })
      .then((response) => {
        console.log(response.data); // Optional: handle the response
        setMarks(initialState); // Clear the form inputs after successful submission
      })
      .catch((error) => {
        console.error(error); // Optional: handle the error
      });
  };

  const inputStyle = {
    marginBottom: '10px',
  };

  const buttonStyle = {
    marginTop: '10px',
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Telugu:
        <input
          type="number"
          name="telugu"
          value={marks.telugu}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>
      <br />
      <label>
        Hindi:
        <input
          type="number"
          name="hindi"
          value={marks.hindi}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>
      <br />
      <label>
        English:
        <input
          type="number"
          name="english"
          value={marks.english}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>
      <br />
      <label>
        Maths:
        <input
          type="number"
          name="maths"
          value={marks.maths}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>
      <br />
      <label>
        Science:
        <input
          type="number"
          name="science"
          value={marks.science}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>
      <br />
      <label>
        Social:
        <input
          type="number"
          name="social"
          value={marks.social}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>
      <br />
      <label>
        Student ID:
        <input
          type="number"
          name="studentId"
          value={marks.studentId}
          onChange={handleChange}
          style={inputStyle}
        />
      </label>
      <br />
      <button type="submit" style={buttonStyle}>
        Submit
      </button>
    </form>
  );
};

export default AddMarksForm;
