import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import StudentForm from './StudentForm';
import AddMarksForm from './AddMarksForm';
import HomePage from './HomePage';
import UpdateMarksForm from './UpdateMarksForm';

const App = () => {
  return (
    <BrowserRouter>
      <div className="app-container">
        <header className="header">
          <h1>Student Management System</h1>
        </header>
        <style>
          {`
          .nav-links {
            display: flex;
            flex-direction: column;
          }
          `}
        </style>
        <nav>
          <ul className="nav-links">
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/StudentForm">Student Management</Link>
            </li>
            <li>
              <Link to="/AddMarksForm">Assign Marks to Student</Link>
            </li>
            <li>
              <Link to="/UpdateMarksForm">Correct Student Marks</Link>
            </li>
          </ul>
        </nav>
        <main>
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/StudentForm" element={<StudentForm />} />
            <Route path="/AddMarksForm" element={<AddMarksForm />} />
            <Route path="/UpdateMarksForm" element={<UpdateMarksForm />} />
            {/* Add more routes for other components if needed */}
          </Routes>
        </main>
        <footer>
          {/* Add your footer content here */}
        </footer>
      </div>
    </BrowserRouter>
  );
};

export default App;
