import React, { useState } from 'react';
import axios from 'axios';

const MarksForm = () => {
  const [studentId, setStudentId] = useState('');
  const [telugu, setTelugu] = useState('');
  const [hindi, setHindi] = useState('');
  const [english, setEnglish] = useState('');
  const [maths, setMaths] = useState('');
  const [science, setScience] = useState('');
  const [social, setSocial] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    const marksData = {
      studentId,
      telugu: parseInt(telugu),
      hindi: parseInt(hindi),
      english: parseInt(english),
      maths: parseInt(maths),
      science: parseInt(science),
      social: parseInt(social)
    };

    try {
      await axios.post('http://localhost:8080/addMarks', marksData);
      // Reset form fields
      setStudentId('');
      setTelugu('');
      setHindi('');
      setEnglish('');
      setMaths('');
      setScience('');
      setSocial('');
      alert('Marks added successfully!');
    } catch (error) {
      console.error('Error adding marks:', error);
      alert('Failed to add marks. Please try again.');
    }
  };

  return (
    <div>
      <h2>Enter Marks</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="studentId">Student ID:</label>
          <input
            type="text"
            id="studentId"
            value={studentId}
            onChange={(event) => setStudentId(event.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="telugu">Telugu:</label>
          <input
            type="number"
            id="telugu"
            value={telugu}
            onChange={(event) => setTelugu(event.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="hindi">Hindi:</label>
          <input
            type="number"
            id="hindi"
            value={hindi}
            onChange={(event) => setHindi(event.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="english">English:</label>
          <input
            type="number"
            id="english"
            value={english}
            onChange={(event) => setEnglish(event.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="maths">Maths:</label>
          <input
            type="number"
            id="maths"
            value={maths}
            onChange={(event) => setMaths(event.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="science">Science:</label>
          <input
            type="number"
            id="science"
            value={science}
            onChange={(event) => setScience(event.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="social">Social:</label>
          <input
            type="number"
            id="social"
            value={social}
            onChange={(event) => setSocial(event.target.value)}
            required
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default MarksForm;
